mapper.insert:
INSERT INTO common_code
(
  level, code, name,
  chk_use, reg_date, reg_user_seq,
  up_date, up_user_seq, top_code
)
VALUES
  (
    #{level}, #{code}, #{name},
    #{chk_use}, #{reg_date}, #{reg_user_seq},
    #{up_date}, #{up_user_seq}, #{top_code}
  );

mapper.update:
UPDATE common_code
SET
  chk_use = #{chk_use}
WHERE code = #{code}
